SELECT u.id, u.device_id deviceId, u.device_name deviceName, u.device_type deviceType, u.device_model deviceModel, u.device_status deviceStatus, u.device_belong deviceBelong, u.effective_year effectiveYear, u.remark remark, u.create_by createBy, u.create_time createTime, u.update_by updateBy, u.update_time updateTime, u.del_flag FROM t_device u WHERE 1 = 1
AND u.device_name = #{device.deviceName}
AND u.device_type = #{device.deviceType}
AND u.device_status = #{device.deviceStatus}
AND u.create_by = #{village.createBy}
AND u.create_time = #{device.createTime}
group by u.device_name
