SELECT u.room_id roomId, u.building_id buildingId, u.room_name roomName, u.room_status status, u.room_type roomType, u.room_area roomArea, u.room_price roomPrice, u.pay_way payWay, u.room_orther roomOther, u.room_pic roomPic, u.map_pic mapPic, u.contract_id contractId, u.room_msg roomMsg, u.remark remark, u.create_by createBy, u.create_time createTime, u.update_by updateBy, u.update_time updateTime, u.del_flag FROM t_room u WHERE 1 = 1
AND u.room_id = #{room.roomId}
AND u.room_name = #{room.roomName}
AND u.room_status = #{room.status}
AND u.room_type = #{room.roomType}
AND u.create_time = #{room.createTime}
group by u.room_name
